import * as type from "../types/auth";

const apiUrl = "http://localhost:3333";

export const login = (formLogin) => async (dispatch) => {
  dispatch({
    type: type.LOGIN_REQUEST,
  });

  try {
    const response = fetch(apiUrl + "/login", {
      method: "GET",
      headers: {
        Accept: "application/json, text/plain, */*",
        "Content-Type": "application/json;charset=UTF-8",
      },
      body: JSON.stringify(formData)
    });

    console.log(data);
    data
      .then((response) => {
        return response.json();
      })
      .then((result) => {
        dispatch({
          type: type.GET_USERS_SUCCESS,
          payload: result,
        });

        if (result.find((user) => user.email === formLogin.email) == null) {
          dispatch({
            type: type.LOGIN_FAILED,
            payload: "Email não encontrado.",
          });
        } else {
          let user = result.find((user) => user.email === formLogin.email);
          if (user.password === formLogin.password) {
            dispatch({
              type: type.LOGIN_SUCCESS,
              payload: user,
            });
          } else {
            dispatch({
              type: type.LOGIN_FAILED,
              payload: "Senha incorreta.",
            });
          }
        }
      });
  } catch (error) {
    dispatch({
      type: type.LOGIN_FAILED,
      payload: "Erro no login.",
    });
  }
};

export const logout = (formLogin) => async (dispatch) => {
  dispatch({
    type: type.SET_LOGOUT,
  });
};

export const registerUser = (values, navigation) => async (dispatch) => {
  dispatch({
    type: type.CREATE_USER_REQUEST,
  });

  try {
    const response = await fetch(apiUrl, {
      method: "POST",
      headers: {
        Accept: "application/json, text/plain, */*",
        "content-type": "application/json; charset=utf-8",
      },
      body: JSON.stringify(values),
    });
    const result = await response.json();
    dispatch({
      type: type.CREATE_USER_SUCCESS,
      payload: result,
    });
    navigation.navigate("Login");
  } catch (error) {
    dispatch({
      type: type.CREATE_USER_FAILED,
      payload: error,
    });
  }
};
