  const [taskDescription, setTaskDescription] = React.useState(
    task?.description
  );
  const dispatch = useDispatch();
  const navigation = useNavigation();

  console.log(2);

  const handleEditTask = () => {
    //navigation.navigate("Editar tarefa", { taskId });
  };

  if (!task) {
    return (
      <View style={styles.container}>
        <Text style={styles.title}>Tarefa não encontrada.</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Text style={styles.textHeader}>ID {formattedID}:</Text>
      <Text style={styles.title}>Título:</Text>
      <Text
        style={{
          ...styles.subtitle,
          textDecorationLine: task.completed ? "line-through" : "none",
        }}
      >
        {task.title}
      </Text>
      <Text style={styles.title}>Descrição:</Text>
      <Text
        style={{
          ...styles.subtitle,
          textDecorationLine: task.completed ? "line-through" : "none",
        }}
      >
        {task.description}
      </Text>
      <TouchableOpacity
        disabled={task.completed ? true : false}
        style={
          !task.completed
            ? styles.buttonContent
            : { ...styles.buttonContent, opacity: 0.7 }
        }
        onPress={handleEditTask}
      >
        <Text style={styles.buttonText}>Editar</Text>
      </TouchableOpacity>
    </View>
  );
};
