import React from "react";
import { View, Text, TouchableOpacity } from "react-native";
import { useSelector, useDispatch } from "react-redux";
import { getTasks } from "../../../redux/actions/taskAction";
import { styles } from "./TaskListPage.style";

export default function TaskListPage() {
  const dispatch = useDispatch();
  const { tasks } = useSelector((state) => state.task);

  React.useEffect(() => {
    dispatch(getTasks());
  }, []);

  const handleMarkTasksAsCompleted = () => {
    const selectedTasks = tasks
      .filter((task) => task.selected)
      .map((task) => task.id);
    dispatch(markTaskCompleted(selectedTasks));
  };

  const handleDeleteSelectedTasks = () => {
    dispatch(deleteSelectedTasks());
  };

  const navigateToDetails = (taskId: number) => {
    navigation.navigate("Detalhes da tarefa", { taskId });
  };

  const onItemPress = (taskId: number) => {
    const time = new Date().getTime();
    const delta = time - lastPress;

    if (delta < doublePressDelay && taskId === currentId) {
      navigateToDetails(taskId);
    } else {
      setLastPress(time);
      setCurrentId(taskId);
      setTimeout(() => {
        setCurrentId(null);
      }, doublePressDelay);
    }
  };

  const renderItem = ({ item }: { item: Task }) => (
    <TouchableOpacity
      onPress={() => handleToggleTaskSelection(item.id)}
      onPressIn={() => onItemPress(item.id)}
    >
      <Text
        style={{
          ...styles.taskIten,
          textDecorationLine: item.completed ? "line-through" : "none",
          color: item.completed ? "gray" : "black",
          backgroundColor: item.selected ? "lightgrey" : "transparent",
        }}
      >
        {item.title}
      </Text>
    </TouchableOpacity>
  );

  const onPressAddTaskScreen = () => {
    navigation.navigate("Adicionar tarefa");
  };

  return (
    <View style={styles.container}>
      <View style={styles.taskToolsHeader}>
        <Text style={styles.description}>
          Selecione as tarefas para executar ações.
        </Text>
      </View>
      <TouchableOpacity
        style={{ ...styles.buttonFloatingContent, backgroundColor: "#097c7c" }}
        onPress={onPressAddTaskScreen}
      >
        <Ionicons name="add" size={30} color="white" />
      </TouchableOpacity>
      <View style={styles.taskToolsHeader}>
        <TouchableOpacity
          style={{ ...styles.buttonContent, backgroundColor: "#014e4e" }}
          onPress={handleMarkTasksAsCompleted}
        >
          <Ionicons name="ios-checkmark-circle" size={20} color="#fff" />
          <Text style={styles.buttonText}>Concluir</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={{ ...styles.buttonContent, backgroundColor: "#4e0101" }}
          onPress={handleDeleteSelectedTasks}
        >
          <MaterialIcons name="delete" size={20} color="#fff" />
          <Text style={styles.buttonText}>Deletar</Text>
        </TouchableOpacity>
      </View>
      <Text style={styles.title}>Tarefa(s):</Text>
      <Text style={styles.description}>
        Duplo clique para acessar a tarefa.
      </Text>
      <FlatList
        data={tasks.filter((task) => !task.completed)}
        renderItem={renderItem}
        keyExtractor={(item) => item.id.toString()}
      />
      <Text style={styles.title}>Concluída(s):</Text>
      <FlatList
        data={tasks.filter((task) => task.completed)}
        renderItem={renderItem}
        keyExtractor={(item) => item.id.toString()}
      />
    </View>
  );
}
