import * as type from "../types/task";

const initialStateTask = {
  tasks: [],
  error: "",
  isLoading: false,
  isFetching: false,
  task: {},
};

export const taskReducer = (state = initialStateTask, action) => {
  switch (action.type) {
    case type.GET_TASKS_REQUEST:
      console.log(1);
      return {
        ...state,
        isFetching: true,
        error: "",
      };
    case type.GET_TASKS_SUCCESS:
      console.log(2);
      console.log(action.payload);
      return {
        ...state,
        isFetching: false,
        tasks: action.payload,
      };
    case type.GET_TASKS_FAILED:
      console.log(3);
      console.log(action.payload);
      return {
        ...state,
        isFetching: false,
        error: action.payload,
      };
      case type.UPDATE_POST_REQUEST:  
                    return { 
                      ...state, 
                      isLoading: true,
                      error: ''
                        };
                  case type.UPDATE_POST_SUCCESS:
                    return { 
                      ...state,
                      isLoading: false,
                      posts: state.posts.map(    
                        (form, i) => form.id === action.payload.id ? {...form, 
                          title : action.payload.title ,  
                          description: action.payload.description,
                          image: action.payload.image,
                          createdAt: action.payload.createdAt,
                        }   : form)  ,
                    };
                  case type.UPDATE_TASK_FAILED:
                    return {
                      ...state,
                      isLoading: false,
                      error: action.payload
                    };
    default:
      console.log(4);
      return state;
  }
};
